import React, {useState,useEffect} from "react";
import { Link } from "react-router-dom";
import axios from "axios"

const RecentBlog = () => {
  // Sample blog data with three categories
 
  
  const [latestPosts, setLatestPosts] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);


  useEffect(() => {
    fetchLatestPosts();
}, []);

const fetchLatestPosts = async () => { 
  try { 
    const response = await axios.get("http://localhost:5000/api/latest-posts");
    setLatestPosts(response.data);
    setLoading(false);

  }catch(error){
  
  }
}

  return (
    <section
      className="recent-blog-section"
      style={{ padding: "60px 0", backgroundColor: "#f6f6f6" }}
    >
      <div className="container">
        <div className="section-header text-center mb-5">
          <h2>Recent Blog Posts</h2>
          <p>Stay updated with our latest insights</p>
        </div>

        <div className="row">
          {recentPosts.map((post) => (
            <div key={post.id} className="col-lg-4 col-md-6 mb-4">
              <div
                className="blog-card"
                style={{
                  backgroundColor: "#fff",
                  borderRadius: "10px",
                  overflow: "hidden",
                  boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
                }}
              >
                <div
                  className="blog-image"
                  style={{
                    height: "200px",
                    overflow: "hidden",
                  }}
                >
                  <img
                    src={post.image}
                    alt={post.title}
                    style={{
                      width: "100%",
                      height: "100%",
                      objectFit: "cover",
                    }}
                  />
                </div>
                <div className="blog-content" style={{ padding: "20px" }}>
                  <span
                    className="category"
                    style={{
                      color: "#174d90",
                      fontWeight: "500",
                      fontSize: "14px",
                    }}
                  >
                    {post.category}
                  </span>
                  <h3
                    style={{
                      fontSize: "18px",
                      marginTop: "10px",
                      lineHeight: "1.4",
                    }}
                  >
                    <Link
                      to={post.link}
                      style={{
                        color: "#333",
                        textDecoration: "none",
                      }}
                    >
                      {post.title}
                    </Link>
                  </h3>
                </div>
              </div>
            </div>
          ))}
        </div>

        <div className="text-center mt-4">
          <Link
            to="/blog"
            className="explore-more-btn"
            style={{
              backgroundColor: "#174d90",
              color: "#fff",
              padding: "10px 25px",
              borderRadius: "5px",
              textDecoration: "none",
              display: "inline-block",
              transition: "background-color 0.3s ease",
            }}
            onMouseOver={(e) => (e.target.style.backgroundColor = "#1a5aa8")}
            onMouseOut={(e) => (e.target.style.backgroundColor = "#174d90")}
          >
            Explore More
          </Link>
        </div>
      </div>
    </section>
  );
};

export default RecentBlog;
