// frontend/src/components/BlogPage.jsx
import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

const BlogPage = () => {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeCategory, setActiveCategory] = useState("sports");

  useEffect(() => {
    fetchCategoryPosts(activeCategory);
  }, [activeCategory]);

  const fetchCategoryPosts = async (category) => {
    try {
      setLoading(true);
      const response = await axios.get(
        `http://localhost:5000/api/posts/${category}`
      );
      setPosts(response.data);
      setLoading(false);
    } catch (err) {
      setError("Failed to fetch posts");
      setLoading(false);
    }
  };

  return (
    <main id="main">
      <div className="container">
        {/* Category Selection */}
        <div className="category-tabs mb-4">
          {["sports", "politics", "entertainment"].map((category) => (
            <button
              key={category}
              className={`btn ${
                activeCategory === category
                  ? "btn-primary"
                  : "btn-outline-primary"
              } me-2`}
              onClick={() => setActiveCategory(category)}
            >
              {category.toUpperCase()}
            </button>
          ))}
        </div>

        {loading ? (
          <div>Loading...</div>
        ) : error ? (
          <div>{error}</div>
        ) : (
          <div className="row gy-4 posts-list">
            {posts.map((post, index) => (
              <div key={index} className="col-xl-4 col-md-6">
                <article>
                  <div className="post-img">
                    <img
                      src={post.image}
                      alt=""
                      className="img-fluid"
                      onError={(e) => {
                        e.target.src = "default-image-url.jpg";
                      }}
                    />
                  </div>

                  <p className="post-category">{post.category}</p>

                  <h2 className="title">
                    <a
                      href={post.link}
                      target="_blank"
                      rel="noopener noreferrer"
                    >
                      {post.title}
                    </a>
                  </h2>

                  <div className="post-meta">
                    <p className="post-date">
                      <time dateTime={post.pubDate}>
                        {new Date(post.pubDate).toLocaleDateString()}
                      </time>
                    </p>
                  </div>
                </article>
              </div>
            ))}
          </div>
        )}
      </div>
    </main>
  );
};

export default BlogPage;
